import React, { useState, useEffect, } from "react";
import { useForm } from "react-hook-form";
import { confirmAlert } from "react-confirm-alert";
import axios from "axios";
import { FETCH } from "../../../Fetch";
import "./contact.css";


export default function Contact() {
  const { register, formState } = useForm();
  const { isSubmitSuccessful } = formState;

  const handleChange = (event) => {
    setFormAll({ ...formAll, [event.target.name]: event.target.value });
  };

  const [formAll, setFormAll] = useState({
    firstname_question: "",
    lastname_question: "",
    mail_question: "",
    mobile_question: "",
    objet_question: "",
    message_question: "",
    isNew: 1,
  });

  const confirmSend = () => {
    document.location.href = "/";
  };

  const postForm = () => {
    confirmAlert({
      title: "Confirmation",
      message: "Confirmez-vous l'envoi de votre formulaire ?",
      buttons: [
        {
          label: "Oui",
          onClick: () => {
            axios.post(`${FETCH}/contact`, formAll).catch(function (error) {
              console.log(error);
            }) && confirmSend();
          },
        },
        {
          label: "Non",
        },
      ],
    });
  };


  return (
    <section className="formularyAbout">
            <form
              onSubmit={(e) => {
                e.preventDefault();
                postForm();
              }}
            >
              <div>
                <h2 className="aboutPage-title">Contac :</h2>
                <label>Nom *</label>
                <input
                  required
                  className="lastName"
                  type="text"
                  placeholder="EX : Dupont"
                  name="lastname_question"
                  ref={register({ required: true, min: 1, maxLength: 50 })}
                  onChange={handleChange}
                />
                <label>Prénom *</label>
                <input
                  required
                  className="firstName"
                  type="text"
                  placeholder="EX : Martin"
                  name="firstname_question"
                  ref={register({ required: true, maxLength: 50 })}
                  onChange={handleChange}
                />
                <label>Téléphone *</label>
                <input
                  className="mobile"
                  type="tel"
                  placeholder="EX : 0601020304"
                  name="mobile_question"
                  ref={register({ required: true })}
                  onChange={handleChange}
                />
                <label>Email *</label>
                <input
                  required
                  className="mail"
                  type="email"
                  placeholder="EX : martin.dupont@gmail.com"
                  name="mail_question"
                  ref={register}
                  onChange={handleChange}
                />
                <label>Objet</label>
                <input
                  className="objet"
                  type="text"
                  placeholder="Objet de votre message"
                  name="objet_question"
                  ref={register({ maxLength: 150 })}
                  onChange={handleChange}
                />
                <label>Message *</label>
                <textarea
                  required
                  className="Votre message"
                  placeholder="Saisir votre message"
                  name="message_question"
                  ref={register({ required: true, maxLength: 7000 })}
                  onChange={handleChange}
                />
                <p className="obligatory">* Champs obligatoire.</p>
                <input type="submit" />
              </div>
              {isSubmitSuccessful && (
                <span className="text-success">
                  <small className="p-3">
                    Your message has been sent successfully !
                  </small>
                </span>
              )}
            </form>
        </section>
  );
};
