import React from "react";
import "contact.css";

const contact = () => {
  return (
    <section className="formularyAbout">
            <form
              onSubmit={(e) => {
                e.preventDefault();
                postForm();
              }}
            >
              <div>
                <h2 className="aboutPage-title">Posez nous votre question :</h2>
                <label>Nom *</label>
                <input
                  required
                  className="lastName"
                  type="text"
                  placeholder="EX : Dupont"
                  name="lastname_question"
                  ref={register({ required: true, min: 1, maxLength: 50 })}
                  onChange={handleChange}
                />
                <label>Pr√©nom *</label>
                <input
                  required
                  className="firstName"
                  type="text"
                  placeholder="EX : Martin"
                  name="firstname_question"
                  ref={register({ required: true, maxLength: 50 })}
                  onChange={handleChange}
                />
                <label>T√©l√©phone *</label>
                <input
                  className="mobile"
                  type="tel"
                  placeholder="EX : 0601020304"
                  name="mobile_question"
                  ref={register({ required: true })}
                  onChange={handleChange}
                />
                <label>Email *</label>
                <input
                  required
                  className="mail"
                  type="email"
                  placeholder="EX : martin.dupont@gmail.com"
                  name="mail_question"
                  ref={register}
                  onChange={handleChange}
                />
                <label>Objet</label>
                <input
                  className="objet"
                  type="text"
                  placeholder="Objet de votre message"
                  name="objet_question"
                  ref={register({ maxLength: 150 })}
                  onChange={handleChange}
                />
                <label>Message *</label>
                <textarea
                  required
                  className="Votre message"
                  placeholder="Saisir votre message"
                  name="message_question"
                  ref={register({ required: true, maxLength: 7000 })}
                  onChange={handleChange}
                />
                <p className="obligatory">* Champs obligatoire.</p>
                <input type="submit" />
              </div>
              {isSubmitSuccessful && (
                <span className="text-success">
                  <small className="p-3">
                    Your message has been sent successfully !
                  </small>
                </span>
              )}
            </form>
        </section>
  );
};

export default contact;
