import React, { useState, useEffect, } from "react";
import { useForm } from "react-hook-form";
import { confirmAlert } from "react-confirm-alert";
import axios from "axios";
import { FETCH } from "../../../Fetch";
import "./contact.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'


export default function Contact() {
  const { register, formState } = useForm();
  const { isSubmitSuccessful } = formState;

  const handleChange = (event) => {
    setFormAll({ ...formAll, [event.target.name]: event.target.value });
  };

  const [formAll, setFormAll] = useState({
    firstname_question: "",
    lastname_question: "",
    mail_question: "",
    mobile_question: "",
    objet_question: "",
    message_question: "",
    isNew: 1,
  });

  const confirmSend = () => {
    document.location.href = "/";
  };

  const postForm = () => {
    confirmAlert({
      title: "Confirmation",
      message: "Confirmez-vous l'envoi de votre formulaire ?",
      buttons: [
        {
          label: "Oui",
          onClick: () => {
            axios.post(`${FETCH}/contact`, formAll).catch(function (error) {
              console.log(error);
            }) && confirmSend();
          },
        },
        {
          label: "Non",
        },
      ],
    });
  };


  return (
    <section className="contact">
      <h1 className="aboutPage-title">Contact :</h1>
            <div>
            <form
              onSubmit={(e) => {
                e.preventDefault();
                postForm();
              }}
            >
              <div>                
                <label>Nom *</label>
                <input
                  required
                  className="lastName"
                  type="text"
                  placeholder="Nom *"
                  name="lastname_question"
                  ref={register({ required: true, min: 1, maxLength: 50 })}
                  onChange={handleChange}
                />
                <label>Prénom *</label>
                <input
                  required
                  className="firstName"
                  type="text"
                  placeholder="Prénom *"
                  name="firstname_question"
                  ref={register({ required: true, maxLength: 50 })}
                  onChange={handleChange}
                />
                <label>Téléphone *</label>
                <input
                  className="mobile"
                  type="tel"
                  placeholder="Téléphone *"
                  name="mobile_question"
                  ref={register({ required: true })}
                  onChange={handleChange}
                />
                <label>Email *</label>
                <input
                  required
                  className="mail"
                  type="email"
                  placeholder="Email *"
                  name="mail_question"
                  ref={register}
                  onChange={handleChange}
                />
                <label>Objet</label>
                <input
                  className="objet"
                  type="text"
                  placeholder="Objet de votre message"
                  name="objet_question"
                  ref={register({ maxLength: 150 })}
                  onChange={handleChange}
                />
                <label>Message *</label>
                <textarea
                  required
                  className="Votre message"
                  placeholder="Saisir votre message"
                  name="message_question"
                  ref={register({ required: true, maxLength: 7000 })}
                  onChange={handleChange}
                />
                <p className="obligatory">* Champs obligatoire.</p>
                <input type="submit" className="btn"/>
              </div>
              {isSubmitSuccessful && (
                <span className="text-success">
                  <small className="p-3">
                    Votre message à bien été envoyé !
                  </small>
                </span>
              )}
            </form>
      <div> 
        <p>Retrouvez moi sur les réseaux sociaux !</p>
        <ul>
            <li><a><FontAwesomeIcon icon={["fab", "github"]} /></a></li>

        </ul>
      </div>
            </div>
        </section>
  );
};
